def accueil
puts "Bonjour et bienvenue dans notre dans notre FAQ journalistes"
puts "Voici la liste des questions, entrez le numéro de la question puis appuyez sur [entrée] pour obtenir la réponse"
puts "1 - Combien y a-t-il de journalistes dans ce array ?"
puts "2 - Combien d'handle contiennent un numéro ?"
puts "3 - Combien d'handle contiennent les 4 lettres du prénom 'Aude' à la suite (sans prendre en compte les majuscules) ?"
puts "4 - Combien commencent par une majuscule (première lettre juste après le @) ?"
puts "5 - Combien contiennent une majuscule ?"
puts "6 - Combien y a-t-il de underscore _ dans tous les pseudos confondus ?"
puts "7 - Trie la liste de handle par ordre alphabétique."
puts "8 - Quels sont les 50 handles les plus courts de ce array ?"
puts "9 - Quelle est la position dans l'array de la personne @epenser ?"
question = gets.chomp.to_i
end

def q1
    a = journalistes
    b = a.length 
    puts "Combien y a-t-il de journalistes dans ce array ? Réponse : #{b}"
end    

def q2
    a = journalistes
    b = 0
    a.map {|i| if i.count("0123456789") > 0 then b=b+1 end} 
    puts "Combien d'handle contiennent un numéro ? Réponse : #{b} "
end

def q3
    a = journalistes
    b = 0
    a.map {|i| if i.include?("aude") == true then b = b + 1 end}
    puts "Combien d'handle contiennent les 4 lettres du prénom 'Aude' à la suite (sans prendre en compte les majuscules) ? Réponse : #{b}"
end

def q4
    a = journalistes
    b = 0
    a.map {|i| if i.index(/[A-Z]/) == 1 then b = b + 1 end}
    puts "Combien commencent par une majuscule (première lettre juste après le @) ? Réponse : #{b}"
end

def q5
    a = journalistes
    b = 0
    puts "Combien contiennent une majuscule ? Réponse : #{a.grep(/[A-Z]/).length}"
    #print b
end

def q6
    a = journalistes
    b = 0
    a.map {|i| if i.count('_') > 0 then b = b + 1 end}
    puts "Combien y a-t-il de underscore _ dans tous les pseudos confondus ? Réponse #{b}"
end

def q7
    a = journalistes
    b = a.sort { |a,b| a <=> b}
    print b
end

def q8
    a = journalistes
    puts "Quels sont les 50 handles les plus courts de ce array ?"
    puts "Voici la liste :"
    print a.min(50) { |a, b| a.length <=> b.length }
    
end

def q9
    a = journalistes
    b = a.index ("@epenser")
    puts "Quelle est la position dans l'array de la personne @epenser ? Réponse : #{b} "
end


def essai(question)
    if question == 1
    q1
    elsif question == 2
    q2
    elsif question == 3
    q3
    elsif question == 4
    q4
    elsif question == 5
    q5
    elsif question == 6
    q6
    elsif question == 7
    q7
    elsif question == 8
    q8
    elsif question == 9
    q9
    else 
        puts "Il n'y a pas de question n°#{question}, rentrez un chiffre valide"
    end

end


def perform
a=accueil
essai(a)
end

